import GetPaid from '../../assets/forEducators/GetPaid.png';
import GetHeard from '../../assets/forEducators/GetHeard.png';
import TeachLearners from '../../assets/forEducators/TeachLearners.png';
import Planet from '../../assets/forEducators/Planet.png';
import SocialMedia from '../../assets/forEducators/SocialMedia.png';
import OwnYourRights from '../../assets/forEducators/OwnYourRights.png';
import SignUp from '../../assets/forEducators/SignUp.png';
import Upload from '../../assets/forEducators/Upload.png';
import Investing from '../../assets/forEducators/Investing.png';


export const perksData = [
	{
		perk: 'Get paid',
		description: 'Earn royalties from your courses same way as artistes earn from their music. The more learners you get, the more money you earn. It\'s that simple.',
		image: GetPaid,
	},
	{
		perk: 'Get heard',
		description:
			'You have so much to say and it is high time you had a platform that gives you a voice. You\'re way too smart for the world not to hear you speak.',
		image: GetHeard,
	},
	{
		perk: 'Teach learners',
		description:
			'There are very few things as wholesome and rewarding as knowing someone considers you their teacher. Helping other people achieve their learning goals is just one of the pros of being an educator.',
		image: TeachLearners,
	},
	{
		perk: 'Become Global',
		description: 'You get to build your personal brand and establish yourself as an expert. You might not become Beyonce of Dwayne Johnson but you can definitely become a global figure in your own right.',
		image: Planet,
	},
	{
		perk: 'Join the Community',
		description: 'Become part of a community of amazing educators and learners. Meet new people, share experiences and learn from them.',
		image: SocialMedia,
	},
	{
		perk: 'Own your rights',
		description: 'You own, in its entirety, the rights to whatever course you create. You control how it\'s used. No questions asked because you are the Boss.',
		image: OwnYourRights,
	},
];
export const processesData = [
	{
		process: 'Sign up',
		description:
			"Click the 'Get started' button and get signed up as an educator.",
		image: SignUp,
	},
	{
		process: 'Record',
		description:
			"Next, you start creating courses it takes 2 - 4 weeks to create a course. Don\'t fret if this is your first time creating an audio course. We would be with you throughout the process.",
		image: SocialMedia,
	},
	{
		process: 'Upload',
		description:
			'When you\'re done creating your course, you upload it. it\'s time to promote and market it to your audience.',
		image: Upload,
	},
	{
		process: 'Earn',
		description:
			'You\'ve done it! Your course is getting a lot of plays. It\'s time for you to reap the fruits of your hard work.',
		image: Investing,
	}
];
